function navbarCollapse(){$("#mainNav").offset().top>100?$("#mainNav").css("background-color","rgba(255, 255, 255, 0.87)"):$("#mainNav").css("background-color","transparent")}function onSizeChange(){$(".navbar-collapse").collapse("hide")}$(function(){navbarCollapse(),$(window).scroll(navbarCollapse),$(window).on("resize",onSizeChange)});